import React from 'react';
import { createRoot } from 'react-dom/client';

// Create a simple App component directly in this file
const App = () => {
  return (
    <div className="app">
      <header className="header">
        <h1>Unlearning Ableism</h1>
        <p className="subtitle">Challenging harmful beliefs about disability</p>
      </header>

      <main className="main-content">
        <div className="intro">
          <h2>Coming Soon</h2>
          <p>
            This interactive web experience is currently being set up.
            Please check back soon to explore ableist beliefs and learn about
            more inclusive perspectives.
          </p>
        </div>
      </main>

      <footer className="footer">
        <p>&copy; {new Date().getFullYear()} Unlearning Ableism</p>
      </footer>
    </div>
  );
};

// Use createRoot API
const container = document.getElementById('root');
const root = createRoot(container);
root.render(<App />);
